import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { getVisualizationData, getColumns } from './api';
import '../styles/App.css';

function DataVisualization({ dataUploaded }) {
    const [columns, setColumns] = useState([]);
    const [selectedColumn, setSelectedColumn] = useState('');
    const [chartData, setChartData] = useState({
        labels: [],
        datasets: []
    });

    useEffect(() => {
        const fetchColumns = async () => {
            try {
                const cols = await getColumns();
                setColumns(cols);
                setSelectedColumn(cols[0]);
            } catch (error) {
                console.error('Error fetching column names:', error);
            }
        };

        if (dataUploaded) {
            fetchColumns();
        }
    }, [dataUploaded]);

    useEffect(() => {
        if (!selectedColumn) return;

        const fetchData = async () => {
            try {
                const data = await getVisualizationData(selectedColumn);
                setChartData({
                    labels: data.labels,
                    datasets: [{
                        label: selectedColumn,
                        data: data.values,
                        backgroundColor: 'rgba(0, 123, 255, 0.5)',
                        borderColor: 'rgba(0, 123, 255, 1)',
                        borderWidth: 1
                    }]
                });
            } catch (error) {
                console.error('Error fetching visualization data:', error);
            }
        };

        fetchData();
    }, [selectedColumn]);

    const handleChange = (event) => {
        setSelectedColumn(event.target.value);
    };

    return (
        <div className="DataVisualization">
            <h2 className="section-title">Data Visualization</h2>
            {!dataUploaded ? (
                <p>Please upload a data file to visualize.</p>
            ) : (
                <>
                    <select onChange={handleChange} value={selectedColumn}>
                        {columns.map(label => (
                            <option key={label} value={label}>{label}</option>
                        ))}
                    </select>
                    <Bar data={chartData} />
                </>
            )}
        </div>
    );
}

export default DataVisualization;
