import React, { useState, useEffect } from 'react';
import { getSummaryData } from './api';

function DataSummary({ dataUploaded }) {
    const [summaryData, setSummaryData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        if (!dataUploaded) {
            setSummaryData(null);
            setError('');
            return;
        }

        const fetchData = async () => {
            setIsLoading(true);
            setError('');
            try {
                const data = await getSummaryData();
                setSummaryData(data);
            } catch (error) {
                console.error('Error fetching data summary:', error);
                setError('Error fetching data summary');
            } finally {
                setIsLoading(false);
            }
        };

        fetchData();
    }, [dataUploaded]);

    const renderSummary = () => {
        if (isLoading) return <p>Loading data summary...</p>;
        if (error) return <p>{error}</p>;
        if (!summaryData) return null;

        return summaryData.columns.map(column => (
            <div key={column}>
                <h3>{column}</h3>
                <p>Data Type: {summaryData.summary[column].dataType}</p>
                <p>Missing Values: {summaryData.summary[column].missing}</p>
                <p>Percentage Missing: {summaryData.summary[column].percentMissing}%</p>
            </div>
        ));
    };

    return (
        <div className="DataSummary">
            <h2>Data Summary</h2>
            {!dataUploaded ? (
                <p>Please upload a data file to view the summary.</p>
            ) : (
                renderSummary()
            )}
        </div>
    );
}

export default DataSummary;
