import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { fetchPreprocessedData } from './api'; // Ensure this path matches your project structure

function DataVisualization() {
    const [chartData, setChartData] = useState({});
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await fetchPreprocessedData();
                setChartData({
                    labels: data.labels,
                    datasets: [{
                        label: 'Dataset',
                        data: data.values,
                        fill: false,
                        borderColor: 'rgb(75, 192, 192)',
                        tension: 0.1
                    }]
                });
                setLoading(false);
            } catch (error) {
                console.error('Error fetching data:', error);
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    return (
        <div className="DataVisualization">
            <div className="section-title">Data Visualization</div>
            {loading ? (
                <p>Loading data...</p>
            ) : Object.keys(chartData).length ? (
                <Line data={chartData} />
            ) : (
                <p>No data available for visualization. Please upload and preprocess data.</p>
            )}
        </div>
    );
}

export default DataVisualization;
