import React, { useState } from 'react';

function Preprocessing() {
    const [isNormalize, setIsNormalize] = useState(false);
    const [handleMissing, setHandleMissing] = useState(false);
    const [processing, setProcessing] = useState(false);
    const [processed, setProcessed] = useState(false);

    const handleNormalizeChange = (e) => {
        setIsNormalize(e.target.checked);
    };

    const handleMissingChange = (e) => {
        setHandleMissing(e.target.checked);
    };

    const submitPreprocessing = async () => {
        setProcessing(true);
        try {
            const response = await fetch('/api/preprocess', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ normalize: isNormalize, handleMissing: handleMissing })
            });

            if (!response.ok) throw new Error('Preprocessing failed');
            setProcessed(true);
        } catch (error) {
            console.error('Error during preprocessing:', error);
            alert('An error occurred during preprocessing.');
        } finally {
            setProcessing(false);
        }
    };

    return (
        <div className="Preprocessing">
            <h2>Data Preprocessing</h2>
            <label>
                <input
                    type="checkbox"
                    checked={isNormalize}
                    onChange={handleNormalizeChange}
                />
                Normalize Data
            </label>
            <br/>
            <label>
                <input
                    type="checkbox"
                    checked={handleMissing}
                    onChange={handleMissingChange}
                />
                Handle Missing Values
            </label>
            <br/>
            <button onClick={submitPreprocessing} disabled={processing}>
                {processing ? 'Processing...' : 'Apply Preprocessing'}
            </button>
            {processed && <p>Preprocessing applied.</p>}
        </div>
    );
}

export default Preprocessing;
