import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { uploadData } from './api';
import '../styles/App.css';

function DataUpload() {
    const [file, setFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [progress, setProgress] = useState(0);
    const [message, setMessage] = useState('');
    const [uploadSuccess, setUploadSuccess] = useState(false);
    const navigate = useNavigate();

    const handleDrop = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
            setFile(e.dataTransfer.files[0]);
            e.dataTransfer.clearData();
        }
    };

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!file) {
            setMessage('Please select a file to upload');
            return;
        }
        try {
            setUploading(true);
            setMessage('');
            await uploadData(file, (event) => {
                setProgress(Math.round((100 * event.loaded) / event.total));
            });
            setMessage('File uploaded successfully');
            setUploadSuccess(true);
        } catch (error) {
            setMessage(`Error uploading file: ${error.message}`);
        } finally {
            setUploading(false);
        }
    };

    const renderPostUploadOptions = () => {
        if (!uploadSuccess) return null;

        return (
            <div className="post-upload-options">
                <p>Data uploaded successfully. What would you like to do next?</p>
                <button onClick={() => navigate('/visualize')}>Visualize Data</button>
                <button onClick={() => navigate('/data-summary')}>View Data Summary</button>
                <button onClick={() => navigate('/preprocess')}>Preprocess Data</button>
            </div>
        );
    };

    return (
        <div className="DataUpload">
            <div className="drag-drop-area" onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}>
                <p>Drag and drop a file here, or click to select a file</p>
                <input type="file" onChange={handleFileChange} disabled={uploading} />
            </div>
            {uploading && <progress value={progress} max="100">{progress}%</progress>}
            {message && <p className="message">{message}</p>}
            <button onClick={handleSubmit} disabled={uploading}>Upload</button>
            {renderPostUploadOptions()}
        </div>
    );
}

export default DataUpload;
