import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { uploadData } from './api';

function DataUpload() {
    const [file, setFile] = useState(null);
    const [uploadSuccess, setUploadSuccess] = useState(false);
    const navigate = useNavigate();

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleUpload = async () => {
        try {
            await uploadData(file);
            setUploadSuccess(true);
        } catch (error) {
            alert(`Error uploading file: ${error.message}`);
        }
    };

    const renderPostUploadOptions = () => {
        if (!uploadSuccess) return null;

        return (
            <div className="post-upload-options">
                <p>Data uploaded successfully. What would you like to do next?</p>
                <button onClick={() => navigate('/visualize')}>Visualize Data</button>
                <button onClick={() => navigate('/data-summary')}>View Data Summary</button>
                <button onClick={() => navigate('/preprocess')}>Preprocess Data</button>
            </div>
        );
    };

    return (
        <div className="DataUpload">
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleUpload}>Upload</button>
            {renderPostUploadOptions()}
        </div>
    );
}

export default DataUpload;
