import React, { useState, useEffect } from 'react';
import { getDataSummary } from './api'; // Make sure to implement this API call

function DataSummary({ filename }) {
    const [summary, setSummary] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (filename) {
            const fetchSummary = async () => {
                try {
                    const data = await getDataSummary(filename);
                    setSummary(data);
                } catch (error) {
                    console.error('Error fetching summary:', error);
                } finally {
                    setLoading(false);
                }
            };

            fetchSummary();
        } else {
            setLoading(false);
        }
    }, [filename]);

    return (
        <div className="DataSummary">
            <div className="section-title">Data Summary</div>
            {loading ? (
                <p>Loading summary...</p>
            ) : summary ? (
                <div>
                    <p>Rows: {summary.rows}</p>
                    <p>Columns: {summary.columns}</p>
                    <h3>Column Summaries:</h3>
                    {Object.entries(summary.column_summaries).map(([column, stats]) => (
                        <div key={column}>
                            <h4>{column}</h4>
                            <ul>
                                {Object.entries(stats).map(([stat, value]) => (
                                    <li key={stat}>{`${stat}: ${value}`}</li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>
            ) : (
                <p>Please upload data to view the summary.</p>
            )}
        </div>
    );
}

export default DataSummary;
