import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import DataUpload from './DataUpload';
import DataVisualization from './DataVisualization';
import DataSummary from './DataSummary';
import Preprocessing from './Preprocessing';
import '../styles/App.css';

function App() {
  const [uploadSuccess, setUploadSuccess] = useState(false);

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>No-Code Deep Learning Platform</h1>
        </header>
        <main>
          <Routes>
            <Route path="/" element={<DataUpload setUploadSuccess={setUploadSuccess} />} />
            {uploadSuccess && (
              <>
                <Route path="/preprocess" element={<Preprocessing />} />
                <Route path="/visualize" element={<DataVisualization />} />
                <Route path="/data-summary" element={<DataSummary />} />
              </>
            )}
          </Routes>
        </main>
        <footer>
          <p>Â© 2023 No-Code Deep Learning. All rights reserved.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
