import React, { useState } from 'react';
import { startPreprocessing } from './api'; // Ensure this path matches your project structure

function Preprocessing() {
    const [normalize, setNormalize] = useState(false);
    const [fillMissing, setFillMissing] = useState(false);
    const [encodeCategorical, setEncodeCategorical] = useState(false);
    const [processingMessage, setProcessingMessage] = useState('');

    const handlePreprocessing = async () => {
        const options = {
            normalize,
            fillMissing,
            encodeCategorical
        };

        try {
            await startPreprocessing(options);
            setProcessingMessage('Data preprocessing completed successfully.');
        } catch (error) {
            setProcessingMessage(`Error during preprocessing: ${error.message}`);
        }
    };

    return (
        <div className="Preprocessing">
            <div className="section-title">Data Preprocessing Options</div>
            <label>
                <input 
                    type="checkbox" 
                    checked={normalize} 
                    onChange={() => setNormalize(!normalize)} />
                Normalize Data
            </label>
            <label>
                <input 
                    type="checkbox" 
                    checked={fillMissing} 
                    onChange={() => setFillMissing(!fillMissing)} />
                Fill Missing Values
            </label>
            <label>
                <input 
                    type="checkbox" 
                    checked={encodeCategorical} 
                    onChange={() => setEncodeCategorical(!encodeCategorical)} />
                Encode Categorical Variables
            </label>
            <button onClick={handlePreprocessing}>Start Preprocessing</button>
            {processingMessage && <p>{processingMessage}</p>}
        </div>
    );
}

export default Preprocessing;
