import React, { useState, useEffect } from 'react';
import { getSummaryData } from './api';

function DataSummary({ dataUploaded }) {
    const [summaryData, setSummaryData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        if (!dataUploaded) {
            setSummaryData(null);
            setError('');
            return;
        }

        const fetchData = async () => {
            setIsLoading(true);
            setError('');
            try {
                const data = await getSummaryData();
                setSummaryData(data);
            } catch (error) {
                console.error('Error fetching data summary:', error);
                setError('Error fetching data summary');
            } finally {
                setIsLoading(false);
            }
        };

        fetchData();
    }, [dataUploaded]);

    const renderSummary = () => {
        if (isLoading) return <p>Loading data summary...</p>;
        if (error) return <p>{error}</p>;
        if (!summaryData) return <p>No data summary available.</p>;

        // Adjust rendering logic based on your data structure
        return (
            <div>
                {summaryData.columns.map(column => (
                    <div key={column}>
                        <h3>{column}</h3>
                        {/* Render additional summary details here */}
                    </div>
                ))}
            </div>
        );
    };

    return (
        <div className="DataSummary">
            <h2>Data Summary</h2>
            {renderSummary()}
        </div>
    );
}

export default DataSummary;
