import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { uploadData } from './api';
import '../styles/App.css';

function DataUpload() {
    const [file, setFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [progress, setProgress] = useState(0);
    const [message, setMessage] = useState('');
    const [uploadSuccess, setUploadSuccess] = useState(false);
    const navigate = useNavigate();

    const handleDrop = (e) => {
        e.preventDefault();
        e.stopPropagation();
        const droppedFile = e.dataTransfer.files[0];
        if (droppedFile && droppedFile.name.endsWith('.csv')) {
            setFile(droppedFile);
            e.dataTransfer.clearData();
        } else {
            setMessage('Only CSV files are accepted');
        }
    };

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        if (selectedFile && selectedFile.name.endsWith('.csv')) {
            setFile(selectedFile);
        } else {
            setMessage('Only CSV files are accepted');
        }
    };

    const handleSubmit = async (e) => {
        // ... (Existing logic remains unchanged)
        setMessage('File uploaded successfully');
        setUploadSuccess(true);
        setDataUploaded(true); // Notify the app that data is uploaded
      };

    const handleNavigation = (path) => {
        navigate(path);
    };

    return (
        <div className="DataUpload">
            <div className="drag-drop-area" onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}>
                <p>Drag and drop a file here, or click to select a file</p>
                <input type="file" onChange={handleFileChange} disabled={uploading} />
            </div>
            {uploading && <progress value={progress} max="100">{progress}%</progress>}
            {message && <p className="message">{message}</p>}
            <button onClick={handleSubmit} disabled={uploading}>Upload</button>
            {uploadSuccess && (
                <div className="post-upload-options">
                    <p>Data uploaded successfully. What would you like to do next?</p>
                    <button onClick={() => handleNavigation('/visualize')}>Visualize Data</button>
                    <button onClick={() => handleNavigation('/data-summary')}>View Data Summary</button>
                    <button onClick={() => handleNavigation('/preprocess')}>Preprocess Data</button>
                </div>
            )}
        </div>
    );
}

export default DataUpload;
