const API_BASE_URL = '/api';

const handleResponse = async (response) => {
    if (!response.ok) {
        const error = await response.text();
        throw new Error(error);
    }
    return response.json();
};

export const uploadData = async (file) => {
    const formData = new FormData();
    formData.append('file', file);
    const response = await fetch(`${API_BASE_URL}/upload`, {
        method: 'POST',
        body: formData,
    });
    return handleResponse(response);
};

export const startPreprocessing = async (filename) => {
    const response = await fetch(`${API_BASE_URL}/preprocess`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ filename })
    });
    return handleResponse(response);
};

// Function to get data summary
export const getDataSummary = async (filename) => {
    const response = await fetch(`${API_BASE_URL}/data-summary`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ filename })
    });

    return handleResponse(response);
};

export const fetchPreprocessedData = async () => {
    const response = await fetch('/api/data');
    return handleResponse(response);
};
