const API_BASE_URL = 'http://127.0.0.1:5000/api';

const handleResponse = async (response) => {
    if (!response.ok) {
        const error = await response.text();
        throw new Error(error);
    }
    return response.json();
};

export const uploadData = async (file, onProgress) => {
    const formData = new FormData();
    formData.append('file', file);

    const response = await fetch(`${API_BASE_URL}/upload`, {
        method: 'POST',
        body: formData,
        onUploadProgress: (event) => {
            if (onProgress) {
                onProgress(event);
            }
        },
    });

    return handleResponse(response);
};

export const startPreprocessing = async (filename) => {
    const response = await fetch(`${API_BASE_URL}/preprocess`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ filename })
    });
    return handleResponse(response);
};

export const getVisualizationData = async (columnName) => {
    const response = await fetch(`${API_BASE_URL}/api/visualization-data`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ columnName })
    });

    if (!response.ok) {
        throw new Error('Error fetching visualization data');
    }

    return response.json();
};

export const getColumns = async () => {
    try {
        const response = await fetch(`${API_BASE_URL}/api/latest-data`, {
            method: 'GET'
        });
        const data = await response.json();
        if (!response.ok) {
            throw new Error(data.error || 'Error fetching column data');
        }
        return data.columns;
    } catch (error) {
        console.error('Error in getColumns:', error);
        throw error;
    }
};

export const getSummaryData = async () => {
    const response = await fetch(`${API_BASE_URL}/api/data-summary`);

    if (!response.ok) {
        throw new Error('Error fetching data summary');
    }

    return response.json();
};

export const fetchPreprocessedData = async () => {
    const response = await fetch('/api/data');
    return handleResponse(response);
};
