import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { getVisualizationData } from './api';

function DataVisualization({ dataUploaded }) {
    if (!dataUploaded) {
      return <div className="DataVisualization">Please upload a data file to visualize.</div>;
    }
    const [selectedColumn, setSelectedColumn] = useState('');
    const [chartData, setChartData] = useState({});

    useEffect(() => {
        if (!selectedColumn) return;

        const fetchData = async () => {
            try {
                const data = await getVisualizationData(selectedColumn);
                setChartData({
                    labels: data.labels,
                    datasets: [{
                        label: selectedColumn,
                        data: data.values,
                        backgroundColor: 'rgba(0, 123, 255, 0.5)',
                        borderColor: 'rgba(0, 123, 255, 1)',
                        borderWidth: 1
                    }]
                });
            } catch (error) {
                console.error('Error fetching visualization data:', error);
            }
        };

        fetchData();
    }, [selectedColumn]);

    const handleChange = (event) => {
        setSelectedColumn(event.target.value);
    };

    return (
        <div className="DataVisualization">
            <h2>Data Visualization</h2>
            <select onChange={handleChange}>
                {chartData.labels.map(label => (
                    <option key={label} value={label}>{label}</option>
                ))}
            </select>
            <Bar data={chartData} />
        </div>
    );
}

export default DataVisualization;
